mapper.select:
SELECT board_no , title , content , writer_id , writer_nick , hit , write_date ,(select count(*) from recommend where board_no = board.board_no) recommend FROM ( SELECT row_number() over (order by board_no desc) R, board.* FROM board
AND ${category} LIKE '%' || #{keyword} || '%'
) board WHERE R between #{pn.startIndex} and #{pn.endIndex}

mapper.update:
UPDATE board 
SET hit = hit + 1
WHERE board_no = #{boardNo}

UPDATE board
SET title = #{title}, content = #{content}
WHERE board_no = #{boardNo}
